
var chartRender = window.chartRender || {};

(function(chartRender,$) {
    chartRender.biz = new function() {
        var $this = this;

        $this.configuration = function() {
            return instances;
        };

        function instances(render_dom, plot_attrs) {
            plot_attrs = plot_attrs || {};
            var self = this;

            var loadingImageData = "data:image/gif;base64,";

            var panelLoading = '<span style="position: absolute; right: 0; top: 0; z-index: 800;">'
                                 +'<img src="'+loadingImageData+'">'
                               +'</span>';

            instances.prototype.showLoading = function () {
                $(render_dom).first().prepend(panelLoading);
            };

            var loading = false;
            instances.prototype.refresh = function(url, format, data) {
                var dtd = $.Deferred();
                loading = true;
                self.showLoading();
                self.getData(url,data)
                .pipe(function(data){
                    if (format){
                        return data;//这里可以做一些格式化之类的事
                    } 
                    return data;
                })
                .done(function(data){
                    loading = false;
                    self.render(data);
                });
                return dtd.promise();
            };
            
            instances.prototype.render =  function (data) {
                self.render_chart(data);
            };
        };
        

        instances.prototype.getData = function (url, data) {
            var deferred = $.Deferred();
            $.ajax({
                type: 'get',
                data: data,
                dataType: 'json',
                url: url,
                // async:false,
                contentType: "application/x-www-form-urlencoded; charset=utf-8",
                success: function(data) {
                    deferred.resolve(data);
                },
                error : function(e) {
                    deferred.reject(e);
                }
            });
            return deferred.promise();
        };

        instances.prototype.render_chart = function (options) {
            options = options || this.plot_attrs || {};
            if (typeof(this.dom)==='object'){
                var plot = echarts.init(this.dom);
                var point = options.point;
                plot.setOption(options);
                plot.resize(plot);
                $(window).resize(function(){
                    if(plot != null && plot != undefined){
                        plot.resize();
                    }
                });
            }
        };
    };
})(chartRender, $);